    private function findLinks($url)
    {
        $this->tempDocument($url);
        $res = [];

        $tmp = $this->findKeywords($this->doc);
        if (!empty($tmp)) $res[$url] = $tmp;


        if (isset($this->doc['a'])) {

            foreach ($this->doc['a'] as $link) {

                $tmp_url = $this->sanitizeLink(pq($link)->attr("href"));
                $tmp_url = str_replace("www.", "", $tmp_url);
                $tmp_url=rtrim($tmp_url,"/");
                $tmp_url_res = str_replace($this->main_url, "", $tmp_url);
                //check if it file
                if (preg_match("/(\w+)(\.\w+)+(?!.*(\w+)(\.\w+)+)/", $tmp_url_res)) continue;

                if ($this->isChild($tmp_url) && !in_array($tmp_url, $this->all_links) && !empty($tmp_url)) {
                    $this->all_links[] = $tmp_url;
                    $res = array_merge($res, $this->findLinks($tmp_url));
                }
            }

        }

        return $res;
    }

    private function findKeywords($doc)
    {
        $res = [];
        foreach ($this->keyword as $keyword){
            $keyword=" ".$keyword." ";

            $elems = $doc["body"]->find(":contains('" . $keyword . "')");
            $elems_text=pq($elems)->text();

            if (!empty($elems_text)) {
                $res[$keyword]=[];
                foreach ($elems as $elem) {
                    $text = pq($elem)->text();
                    $strings = explode("\n", $text);
                    foreach ($strings as $str) {

                        $str=trim($str);

                        if (stripos($str, $keyword) !== false) {
                            if (!in_array($str, $res[$keyword]))
                                $res[$keyword][] = $str;

                        }
                    }
                }
            }

        }

        if (empty($res)) return false;
        else return $res;

    }

    private function getHost($url)
    {
        $url_info = parse_url($url);
        if(isset($url_info["host"]))
        return trim(str_replace("www.", "", $url_info["host"]));

        return false;
    }

    private function sanitizeLink($link)
    {
        $url = trim($link);
        $url = preg_replace("/\#.*|\/\#.*/", "", $url);

        if (preg_match("/^\/.*/", $url))
            $url = $this->main_url . $url;


        return $url;
    }

    private function isChild($url)
    {
        $tmp_host = $this->getHost($url);
        if ($this->main_host == $tmp_host) return true;

        return false;
    }

    private function readFromFile($filename)
    {
        $data_array = [];

        if (($handle = fopen($filename, "r")) !== FALSE) {
            while (($data = fgetcsv($handle, 1000, ",")) !== FALSE) {

                if (!empty($data[0])) {

                    $tmp = trim($data[0]);
                    $tmp = str_replace("www.", "", $tmp);
                    $tmp=rtrim($tmp,"/");
                    $data_array[] = $tmp;
                }
            }
            fclose($handle);
        }

        return $data_array;
    }

