    public function parse()
    {
        if (!empty($this->urls)) {

            foreach ($this->urls as $url) {

                $this->main_url = $url;
                $this->main_host = $this->getHost($url);

                $data = $this->findLinks($url);
                $this->results[$this->main_url] = $data;

            }

           return $this->saveToExcel();

            //print("<pre>".print_r($this->results,true)."</pre>");

        } else $this->debug("Urls list not set");

    }


    protected function validDocument()
    {
        if (stripos($this->doc->getText(), $this->config['valid']) !== FALSE || empty($this->config['valid'])) return true;
        return false;
    }

    public function getActiveProxy()
    {
        //get active proxy for this domain
        //if no proxy was used, then it will return false
        if (getConfig("useProxy")) {
            $this->proxy = ProxyList::getActive($this->url);
            if ($this->proxy) $this->proxyType = CURLPROXY_HTTP;
        }
        //if we already have activated tor, then use it
        if (getConfig('useTor')) {
            //IMPORTANT: instead of Tor we use one single proxy
            if (getConfig('ProxyStarted')) $this->proxy = "84.43.90.42:24409";
            else $this->proxy = false;
            //$this->proxy=TorProxy::get();
            if ($this->proxy) $this->proxyType = 5;

        }
    }

    public function switchProxy()
    {
        if (file_exists(getConfig('CookieJar'))) unlink(getConfig('CookieJar'));

        //depending on config
        //we either get proxies
        if (getConfig("useProxy")) {
            //get random proxy
            $this->proxy = ProxyList::getRandom();
            //and remember it for this domain
            ProxyList::setActive($this->url, $this->proxy);
            $this->proxyType = CURLPROXY_HTTP;
        } //or get tor proxy
        else if (getConfig('useTor')) {
            $this->proxy = "84.43.90.42:24409";
            setConfig('ProxyStarted', true);
            $this->proxyType = 5;

        } else {
            $this->proxy = false;
        }

    }


    //get document
    //$noId - either check for id or not - useful, when id is found inside the document, rather than inside the url
    //$proxy_tries - how many times we have tried to access the document with different proxies
    //$noProxy - do not try to access document through proxy
    //$json - post data that needs to be sent via the request
    protected function getDocument($noId = false, $proxy_tries = 0, $noProxy = false, $json = false, $header = false, $cookie = false)
    {


        //tor uses socks5, for everything else we use http
        $this->proxyType = false;

        //if we want to use proxies or tor
        //mark so in config "useProxy" or "useTor"
        if (!getConfig("useProxy") && !getConfig("useTor")) $noProxy = true;
        if ($noProxy) $this->debug("No proxy nor tor");

        $this->debug("Proxy tries: $proxy_tries");
        $url = $this->url;
        //append if we use proxies - so that we won't use item from cache
        $append = "";
        $this->getActiveProxy();

        //if last getDocument failed
        if ($proxy_tries > 0) {
            //it it was recaptcha and it was solved, then don't switch proxy
            $this->switchProxy();
        }

        //if we have a proxy in the config, then we must use it
        if (getConfig('ProxyServer')) {
            $this->proxy = getConfig('ProxyServer');
            $this->proxyType = getConfig('ProxyServerType');
            $noProxy = false;
        }
        //append a hashtag to url - so that cached version will have different file for each proxy
        if ($this->proxy) {
            //if(getConfig('ProxyServer')) $append="#".md5($this->proxy.$proxy_tries);
            //else $append="#".md5($this->proxy);
        }

        //now we also test for initCall (with current proxy)
        if (!$this->getConfig('initCall' . $append) && method_exists($this, "initCall")) {
            $this->setConfig('initCall' . $append, true);
            $this->initCall();
        }

        $customCookie = false;
        //getting cookie and header after initcall, since they may have been updated there
        if (!$header && method_exists($this, "getHeader") && $this->getHeader() !== FALSE) {
            $header = $this->getHeader();
        }
        if (!$cookie && method_exists($this, "getCookie") && $this->getCookie() !== FALSE) {
            $this->debug("Setting custom cookie");
            $cookie = $this->getCookie();
            $customCookie = true;
        }

        //if we do not have any document
        if (is_null($this->doc) || !$this->doc->isLoaded() || !$this->validDocument()) {
            if (is_null($this->doc)) $this->doc = new MyDocWrap("");
            $url .= $append;
            if ($this->getConfig("require_referer") && !$header) {
                $header = array(
                    'Accept: text/html,application/xhtml+xml,application/xml;q=0.9,image/webp,*/*;q=0.8',
                    'Accept-Language: en-US,en;q=0.8,ru;q=0.6',
                    'Cache-Control:max-age=0',
                    'Connection:keep-alive',
                    'Referer:' . $this->getConfig("require_referer"));
            }

            //getting data
            $data = getDocument($url, $this->proxy, getConfig('DEBUG'), $this->proxyType, $json, $header, $cookie);
            //if there was an error (empty document does not mean an error! )
            //and we can use proxies/tors
            if ($data === FALSE && $proxy_tries < 5 && !$noProxy) {
                $this->debug("Empty document");
                $this->doc->clear();
                deleteMyCache($url);
                if ($customCookie) $this->getDocument($noId, $proxy_tries + 1, $noProxy, $json, $header, false);
                else $this->getDocument($noId, $proxy_tries + 1, $noProxy, $json, $header, $cookie);
            } else {
                $this->doc = new MyDocWrap($data);

                if (getConfig("ALLOWCACHE") && empty($data)) {
                    //if(empty($data))$data=0;
                    $cacheDir = __DIR__ . "/../utils/cache/";
                    if (isConfig("cacheDir")) $cacheDir = getConfig("cacheDir");

                    $md5 = md5($url . $json);
                    $fh = fopen($cacheDir . $md5, 'w');
                    if ($fh) {
                        fwrite($fh, $data);
                        fclose($fh);
                    }
                }

                $this->doc_error = false;
                //if we got an url in the doc
                if ($this->doc->getUrl()) {
                    //then use it as our url (maybe we got redirected)
                    $this->url = $this->doc->getUrl();
                }
                //if document is not valid 				//tried it less than 5 times
                //or recaptcha was found (and solved?)
                else if (!$this->validDocument()) {

                    $this->blocked = false;
                }
            }
        }
    }

