    //can also pass an array, such as array("variations", "text")


    //not really needed anymore
    public function freeMemory()
    {
        if ($this->doc->isLoaded()) $this->doc->unloadDocument();
        $this->doc->clear();
    }

    public function tempDocument($url = false, $noProxy = false, $json = false, $header = false, $cookie = false)
    {
        if ($url) {
            $this->setConfig("oldValid", $this->getConfig("valid"));
            $this->setConfig("valid", "");
            $this->oldUrl = $this->url;
            $this->doc->clear();
            $this->url = $url;
            $this->getDocument(true, 0, $noProxy, $json, $header, $cookie);

        } else {
            $this->setConfig("valid", $this->getConfig("oldValid"));
            $this->url = $this->oldUrl;
            $this->oldUrl = false;
            $this->doc->clear();
            $this->getDocument(false);
        }

    }


    protected function getCookie()
    {
        return $this->cookie;
    }

